#include %occInclude
SHELL new #; here goes interactive entry set pattern=##class(%Regex.Matcher).%New("\b[^ ]+\b|\""([^\""]+)\""") #; aliases for commands f i=1:1 { set line=$text(ALIASES+i) quit:$e(line)'=" " set command=$zstrip($piece(line,";",2),"<>WCP") f j=3:1:$l(line,";") { set alias=$zcvt($piece(line,";",j),"U") continue:alias="" continue:$data(aliases(alias)) set aliases(alias)=command } } for { write !,"CPM>> " read line set line=$zstrip(line,"<>WC")
set command=$piece(line," ") set line=$extract(line,$find(line," ")+1,*) kill args set args=0 set pattern.Text=line while pattern.Locate() { set argument=$zstrip(pattern.Group,"<>WC") set args($increment(args))=$zstrip(argument,"<>","""") }
continue:command="" set command=$zconvert(command,"U") if $d(aliases(command),command)
quit:command="QUIT"
d BATCH(command, args...) } quit BATCH(command, args...) ; here goes batch entry if $text(@command)="" { write !,"Unrecognized command: ",command,! quit } try { set command=command_"(args...)" do @command } catch { write !,"Error occured: ",$zerror } quit ALIASES ;QUIT;Q ;INSTALL;I ;HELP;? ;LIST;L COMMANDS INSTALL(packages...) ;;install package new (packages) if '$d(packages) { write !,"Nothing to install" quit }
write !,"Installing packages: " for i=1:1:packages { set package=packages(i) write !?3,package } quit LIST(args...) ;;list of installed packages new (args) set sql=##class(%SQL.Statement).%New() set tSC=sql.%Prepare("SELECT Name, Version, Description FROM CPM.Package") quit:'$$$ISOK(tSC) set tRS=sql.%Execute() while tRS.%Next() { w !?3,tRS.Name,?15,tRS.Version,?25,tRS.Description } quit HELP(args...) ;;this help new (args) write !,"Cache Package Manager" write !!,"Available commands:" for i=1:1 { set line=$text(COMMANDS+i) quit:line=""
continue:$zstrip($extract(line),"*WCP")="" set help=$piece(line,";;",2) continue:help="" set command=$zcvt($piece(line,"("),"L") write !?3,command,?15,help } w !?3,"quit",?15,"quit from shell" w ! quit
