/* <Manifest> <Package Name="CPM-Installer-Sample" Version = "0.5.0" License="MIT" Path="https://github.com/intersystems/monlbl-viewer.git" /> <Dependencies> <Dependency Name="async" Version=">= 0.2.10"/> <Dependency Name="language-cos" Path="https://github.com/UGroup/atom-language-cos.git"/> </Dependencies> </Manifest>
get loaded as local array
%xmltree("Manifest")=2 %xmltree("Manifest",1,"Package","License")="MIT" %xmltree("Manifest",1,"Package","Name")="CPM-Installer-Sample" %xmltree("Manifest",1,"Package","Path")="https://github.com/intersystems/monlbl-viewer.git" %xmltree("Manifest",1,"Package","Version")="0.5.0" %xmltree("Manifest",2,"Dependencies")=2 %xmltree("Manifest",2,"Dependencies",1,"Dependency","Name")="async" %xmltree("Manifest",2,"Dependencies",1,"Dependency","Version")=">= 0.2.10" %xmltree("Manifest",2,"Dependencies",2,"Dependency","Name")="language-cos" %xmltree("Manifest",2,"Dependencies",2,"Dependency","Path")="https://github.com/UGroup/atom-language-cos.git" */

Method.Implementation:
do ##class(%XML.TextReader).ParseFile(..FileName,. ..XReader)

#dim modeStack As %ArrayOfDataTypes
#dim arrayMode As %Boolean
$$$INITSTACK(modeStack)

while ..XReader.Read() {
    // opening tag e.g. <Package>
    if ..XReader.NodeType = "element" {
        // push tag
        set arrayMode =  $$$TOPSTACK(modeStack)
        if (arrayMode) {
            set LocalName = $name(@LocalName@($increment(@LocalName),..XReader.Name))
        } else {
            set LocalName = $name(@LocalName@(..XReader.Name))
        }
        // we are switching to "array mode" if there is no
        // single attribute in the current tag 
        set arrayMode = '(..XReader.AttributeCount > 0)
        $$$PUSHVALUE(modeStack,arrayMode)
        if arrayMode {
            set @LocalName = 0
        }
    }
    // closing tag e.g. </Package>
    elseif ..XReader.NodeType = "endelement" {
        // pop last tag
        // FIXME -make sure that the last popped subscript is the same as in ..XReader.Name
        $$$POPVALUE(modeStack)
        set arrayMode =  $$$TOPSTACK(modeStack)
        set LocalName = $name(@LocalName,$qlength(LocalName) - (arrayMode + 1))
    }
    // xml tag attributes e.g. <Package Name="CPM-Installer-Sample" Version = "0.5.0" />
    for i=1:1:..XReader.AttributeCount {
        do ..XReader.MoveToAttributeIndex(i)
        set @LocalName@(..XReader.Name) = ..XReader.Value
    }
}
//zw @LocalName
